package litestream_test

import (
	"encoding/binary"
	"encoding/hex"
	"testing"

	"github.com/benbjohnson/litestream"
)

func TestChecksum(t *testing.T) {
	// Ensure a WAL header, frame header, & frame data can be checksummed in one pass.
	t.Run("OnePass", func(t *testing.T) {
		if input, err := hex.DecodeString(""); err != nil {
			t.Fatal(err)
		} else if chksum, err := litestream.Checksum(binary.LittleEndian, 0, input); err != nil {
			t.Fatal(err)
		} else if got, want := chksum, uint64(0xdc2f3e84540488d3); got != want {
			t.Fatalf("Checksum()=%x, want %x", got, want)
		}
	})

	// Ensure we get the same result as OnePass even if we split up into multiple calls.
	t.Run("Incremental", func(t *testing.T) {
		// Compute checksum for beginning of WAL header.
		if chksum, err := litestream.Checksum(binary.LittleEndian, 0, MustDecodeHexString("377f0682002de218000010000000000052382eac857b1a4e")); err != nil {
			t.Fatal(err)
		} else if got, want := chksum, uint64(0x81153b6587178e8f); got != want {
			t.Fatalf("Checksum()=%x, want %x", got, want)
		}

		// Continue checksum with WAL frame header & frame contents.
		if chksum1, err := litestream.Checksum(binary.LittleEndian, 0x81153b6587178e8f, MustDecodeHexString("0000000200000002")); err != nil {
			t.Fatal(err)
		} else if chksum2, err := litestream.Checksum(binary.LittleEndian, chksum1, MustDecodeHexString(``)); err != nil {
			t.Fatal(err)
		} else if got, want := chksum2, uint64(0xdc2f3e84540488d3); got != want {
			t.Fatalf("Checksum()=%x, want %x", got, want)
		}
	})
}

func MustDecodeHexString(s string) []byte {
	b, err := hex.DecodeString(s)
	if err != nil {
		panic(err)
	}
	return b
}
